package com.trivadis.boot.sec;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Configuration;import org.springframework.core.Ordered;import org.springframework.core.annotation.Order;import org.springframework.security.config.annotation.authentication.builders.AuthenticationManagerBuilder;import org.springframework.security.config.annotation.web.builders.HttpSecurity;import org.springframework.security.config.annotation.web.configuration.EnableWebSecurity;import org.springframework.security.config.annotation.web.configuration.WebSecurityConfigurerAdapter;import org.springframework.security.core.userdetails.UserDetailsService;@Configuration@EnableWebSecurity@Order(Ordered.LOWEST_PRECEDENCE - 6)public class WebSecurityConfig extends WebSecurityConfigurerAdapter {	@Autowired	private UserDetailsService userDetailsService;	@Override	protected void configure(HttpSecurity http) throws Exception {		http.httpBasic().realmName("Trivadis Techevent");	}	@Override	protected void configure(AuthenticationManagerBuilder authManagerBuilder) throws Exception {		authManagerBuilder.userDetailsService(userDetailsService);	}}